 function david_mutex() {

    var obj = {};

    var _locked = false;

    var queue = [];

    obj.acquire = function(callback) {

        if(!_locked) {
            _locked = true;
            callback();
        }
        else {
            queue.push(callback);
        }
    }

    obj.release = function() {

        if(queue.length > 0) {
            // console.log("MUTEX: unshifting.")
            // Do not run immeadiatly
            setTimeout(queue.shift(), 0);
        }
        else if(queue.length === 0) {
            _locked = false;
        }
        
    }

    return obj;
};

